vxml.script:
//相似度比较
function needConfirm(param){
	param = parseFloat(param);
	if(param >= 0.84)
		return 1;
	else
		return 0;
}

//异常信息处理
function errorReply(param){
	if(param != 'BillCheck' && param != 'CurrentBill' && param != 'UnpaidBill' && param != 'CreditCheck' && param != 'CreditLimitChange' && param != 'CreditFixedChange' && param != 'CreditTempChange' && param != 'InquiryPW' && param != 'TradePW' && param != 'CardLostReport' && param != 'CardApplicationInqury' && param != 'CashWithdrawFee' && param != 'PointsRule' && param != 'YoungCard' && param != 'InstallmentFee' && param != 'AcceCardIntro' && param != 'InquiryPWIntro' && param != 'BillDate' && param != 'PenaltyIntro' && param != 'Complaints' && param != 'Operator' && param != 'unknown' && param != 'AcceCardApply')
		return 10;	
	else
		return 11;
}
